function constructAlarmAddressSpace(server, addressSpace, eventObjects, done) {
    // server = the created node-opcua server
    // addressSpace = script placeholder
    // eventObjects = to hold event variables in memory from this script

    // internal global sandbox objects are 
    // node = node of the flex server, 
    // coreServer = core iiot server object for debug and access to nodeOPCUA,
    // and scriptObjects to hold variables and functions
    const LocalizedText = opcua.LocalizedText;
    const namespace = addressSpace.getOwnNamespace();

    coreServer.internalDebugLog('Inicio dinámico de espacio de direcciones')
    node.warn('Construyendo espacio de direcciones para servidor de OPC UA en 55489')

    const Variant = opcua.Variant;
    const DataType = opcua.DataType;
    const DataValue = opcua.DataValue;

    coreServer.internalDebugLog("init dynamic address space");
    const rootFolder = addressSpace.findNode("RootFolder");

    const myDevice = namespace.addFolder(rootFolder.objects, {
        "browseName": "Mis_Variables_libres"
    });

    // Variables to store the current values
    let varBoolValue = true;
    let varIntValue = -3649;
    let varDoubleValue = 10.3;
    let varStringValue = "Hola Mundo MI4.0";

    namespace.addVariable({
        organizedBy: myDevice,
        browseName: "Var_BOOL",
        nodeId: "ns=1;s=Var_BOOL",
        dataType: "Boolean",
        value: {
            get: function () {
                return new opcua.Variant({ dataType: opcua.DataType.Boolean, value: varBoolValue });
            },
            set: function (variant) {
                varBoolValue = variant.value;
                return opcua.StatusCodes.Good;
            }
        }
    });

    namespace.addVariable({
        organizedBy: myDevice,
        browseName: "Var_INT",
        nodeId: "ns=1;s=Var_INT",
        dataType: "Int16",
        value: {
            get: function () {
                return new opcua.Variant({ dataType: opcua.DataType.Int16, value: varIntValue });
            },
            set: function (variant) {
                varIntValue = variant.value;
                return opcua.StatusCodes.Good;
            }
        }
    });

    namespace.addVariable({
        organizedBy: myDevice,
        browseName: "Var_Double",
        nodeId: "ns=1;s=Var_Double",
        dataType: "Double",
        value: {
            get: function () {
                return new opcua.Variant({ dataType: opcua.DataType.Double, value: varDoubleValue });
            },
            set: function (variant) {
                varDoubleValue = variant.value;
                return opcua.StatusCodes.Good;
            }
        }
    });

    namespace.addVariable({
        organizedBy: myDevice,
        browseName: "Var_STRING",
        nodeId: "ns=1;s=Var_STRING",
        dataType: "String",
        value: {
            get: function () {
                return new opcua.Variant({ dataType: opcua.DataType.String, value: varStringValue });
            },
            set: function (variant) {
                varStringValue = variant.value;
                return opcua.StatusCodes.Good;
            }
        }
    });

    coreServer.internalDebugLog("Creación dinámica de espacio de direcciones lista");
    node.warn("Construcción de espacio de direcciones para servidor de OPC UA en 55489 listo");

    done();
}

