const min = msg.topic === "minValue" ? parseFloat(msg.payload) : flow.get('min') || -5;
const enable = flow.get('enable') || false;

// Guardar los valores en el contexto de flujo para uso futuro
if (msg.topic === "maxValue") {
    flow.set('max', max);
} else if (msg.topic === "minValue") {
    flow.set('min', min);
}

const amplitude = (max - min) / 2;
const offset = (max + min) / 2;
const frequency = 0.7; // Frecuencia en Hz
const currentTime = Date.now();

// Cálculo de la señal senoidal
const period = 1000 / frequency;
const angle = 2 * Math.PI * (currentTime % period) / period;
    msg.payload = amplitude * Math.sin(angle) + offset;

return msg;

