                return opcua.StatusCodes.Good;
            }
        }
    });

    namespace.addVariable({
        organizedBy: myDevice,
        browseName: "Var_Double",
        nodeId: "ns=1;s=Var_Double",
        dataType: "Double",
        value: {
            get: function () {
                return new opcua.Variant({ dataType: opcua.DataType.Double, value: varDoubleValue });
            },
            set: function (variant) {
                varDoubleValue = variant.value;
                return opcua.StatusCodes.Good;
            }
        }
    });

    namespace.addVariable({
        organizedBy: myDevice,
        browseName: "Var_STRING",
        nodeId: "ns=1;s=Var_STRING",
        dataType: "String",
        value: {
            get: function () {
                return new opcua.Variant({ dataType: opcua.DataType.String, value: varStringValue });
            },
            set: function (variant) {
                varStringValue = variant.value;
                return opcua.StatusCodes.Good;
